import React, {Component} from 'react';

async function fetch_query(q)
    {
        let res

        res = await fetch(q)

        let results = await res.json()
        console.log(results)
        return results;
        
    }

class Listing extends Component {

    // let result_master

    constructor(props) {
        super(props);
        this.state = 
        { 
            query : 'https://www.breakingbadapi.com/api/characters?limit=10', 
            result : []
        }

    }

    componentDidMount(){
        fetch(this.state.query).then(res =>res.json()).then(results => {
            this.setState({result : results})
        })

        console.log(this.state.result)
    }

    handleClick = () =>
    {
       let result1 =  fetch_query(this.state.query)

       this.setState({
           result : result1
       })
        // result_master = result1

    }

    render() {

        return (
            <div>
                <button onClick = {this.handleClick}>Find</button>
                {
                    this.state.result.map(x =><p>{x.name}</p>)

                }
            </div>
            
        
        );
    }
}
 
export default Listing;
