import React, {Component} from 'react';

async function fetch_query(q)
    {
        let res

        res = await fetch(q)

        let results = await res.json()
        console.log(results)
        return results;
        
    }

class Listing extends Component {

    // let result_master

    constructor(props) {
        super(props);
        this.state = 
        { 
            query : 'https://www.breakingbadapi.com/api/characters?limit=10', 
            result : [],
            pagecount : 0
        }

    }

    componentDidMount(){
        fetch(this.state.query).then(res =>res.json()).then(result => {
            this.setState({result})
        })

        // console.log(this.state.result)
    }

    handlePrev = () =>
    {
        this.setState({
            pagecount : this.state.pagecount - 1}, 
            () => {let result1 =  fetch_query(this.state.query + '&offset=' + this.state.pagecount*10)
            this.setState({
             result : result1
             })
         })

    }

    handleNext = () =>
    {
       this.setState({
           pagecount : this.state.pagecount + 1}, 
           () => {let result1 =  fetch_query(this.state.query + '&offset=' + this.state.pagecount*10)
           console.log(result1)
           this.setState({
            result : result1
            },() => {console.log(this.state.result)
            
            })
        })
        

    }

    render() {

        return (
            <div>
                <button onClick = {this.handlePrev}>Prev</button><button onClick = {this.handleNext}>Next</button>
                {
                    this.state.result.map(x =><p>{x.name}</p>)

                }
            </div>
            
        
        );
    }
}
 
export default Listing;
