import React, {Component} from 'react';
import Charrow from './Charrow'

async function fetch_query(q)
    {
        let res

        res = await fetch(q)

        let results = await res.json()
        // console.log(results)
        return results;
        
    }

class Listing extends Component {

    // let result_master

    constructor(props) {
        super(props);
        this.state = 
        { 
            query : 'https://www.breakingbadapi.com/api/characters', 
            result : [],
            pagecount : 0,
            profilename : 'Walter White',
            profile : [1,],
            length : 0
        }
        // this,handleNext = this.handleNext.bind(this)
        // this,handlePrev = this.handlePrev.bind(this)
    }

    componentDidMount= () => {
        fetch(this.state.query+'?limit=10').then(res =>res.json()).then(result => {
            this.setState({result})
        })

        fetch(this.state.query+'?limit=1').then(prof =>prof.json()).then(prof=> {
            this.setState({profile: prof},() =>{
                console.log(this.state.profile)
                this.setState({profilename:this.state.profile[0].name}, () => {console.log(this.state.profilename)})
            })
        })

        // console.log(this.state.result)
    }

    handlePrev = () =>
    {
        this.setState({
            pagecount : this.state.pagecount - 1}, 
            () => {fetch(this.state.query+'?limit=10&offset=' + this.state.pagecount*10 + '&name='+this.props.search+'&category='+this.props.category.replace('/ /g','+')).then(res =>res.json()).then(result => {
                this.setState({result})
            })
         })

    }

    handleNext = () =>
    {
       this.setState({
           pagecount : this.state.pagecount + 1}, 
           () => {fetch(this.state.query+'?limit=10&offset=' + this.state.pagecount*10 +'&name='+this.props.search+'&category='+this.props.category.replace('/ /g','+')).then(res =>res.json()).then(result => {
            this.setState({result})
        })
        })
        

    }

    profilerender = (name) => {

        console.log(name)

        let name_split = name.replace('/ /g','+')

        this.setState({profilename: name}, 
            () => {fetch(this.state.query+'?name='+name_split).then(prof =>prof.json()).then(prof=> {
                this.setState({profile: prof},() =>{
                    console.log(this.state.profile)
                })
            })

            })

    }

    search = () => {

        fetch(this.state.query+'?limit=10'+'&name='+this.props.search+'&category='+this.props.category.replace('/ /g','+') + '&offset=' + this.state.pagecount*10).then(res =>res.json()).then(result => {
            this.setState({result})
        })

    }

    render() {

        // const {img,name,dob,occ,stat,nick,act,seas,cat} = this.state.profile[0]

        return (
            <div>
                <button onClick = {this.handlePrev}>Prev</button><button onClick = {this.handleNext}>Next</button>
                <div>
                    <div className = 'col-sm-6'>{
                    this.state.result.map(x =><Charrow key={x.id} var = {x} profilerend = {this.profilerender}/>)

                    }   </div>
                    <div className = 'col-sm-6'>
                    <div className='row'>
                        <div className='col-sm-6'><img src={this.state.profile[0].img} ></img></div>
                        <div className='col-sm-6'>{this.state.profile[0].name}</div>
                    </div>
                    <div className='row'>{this.state.profile[0].birthday}</div>
                    <div className='row'>{this.state.profile[0].occupation}</div>
                    <div className='row'>{this.state.profile[0].status}</div>
                    <div className='row'>{this.state.profile[0].nickname}</div>
                    <div className='row'>{this.state.profile[0].potrayed}</div>
                    <div className='row'>{this.state.profile[0].appearance}</div>
                    <div className='row'>Quotes</div>    
                    
                    </div>    
                
                </div>
            </div>
            
        
        );
    }
}
 
export default Listing;
