import React, {Component} from 'react';
import Charrow from './Charrow'

async function fetch_query(q)
    {
        let res

        res = await fetch(q)

        let results = await res.json()
        // console.log(results)
        return results;
        
    }

class Listing extends Component {

    // let result_master

    constructor(props) {
        super(props);
        this.state = 
        { 
            query : 'https://www.breakingbadapi.com/api/characters?limit=10', 
            result : [],
            pagecount : 0
        }
        // this,handleNext = this.handleNext.bind(this)
        // this,handlePrev = this.handlePrev.bind(this)
    }

    componentDidMount(){
        fetch(this.state.query).then(res =>res.json()).then(result => {
            this.setState({result})
        })

        // console.log(this.state.result)
    }

    handlePrev = () =>
    {
        this.setState({
            pagecount : this.state.pagecount - 1}, 
            () => {fetch(this.state.query+'&offset=' + this.state.pagecount*10).then(res =>res.json()).then(result => {
                this.setState({result})
            })
         })

    }

    handleNext = () =>
    {
       this.setState({
           pagecount : this.state.pagecount + 1}, 
           () => {fetch(this.state.query+'&offset=' + this.state.pagecount*10).then(res =>res.json()).then(result => {
            this.setState({result})
        })
        })
        

    }

    render() {

        return (
            <div>
                <button onClick = {this.handlePrev}>Prev</button><button onClick = {this.handleNext}>Next</button>
                <div>
                    <div className = 'col-sm-6'>{
                    this.state.result.map(x =><div><Charrow var = {x}/><div class = 'row'></div></div>)

                    }   </div>
                    <div className = 'col-sm-6'>
                    <div className='row'>
                        <div className='col-sm-6'>IMG</div>
                        <div className='col-sm-6'>Name</div>
                    </div>
                    <div className='row'>DOB</div>
                    <div className='row'>OCC</div>
                    <div className='row'>Status</div>
                    <div className='row'>Nickname</div>
                    <div className='row'>Actor</div>
                    <div className='row'>Seasons</div>
                    <div className='row'>Quotes</div>    
                    
                    </div>    
                
                </div>
            </div>
            
        
        );
    }
}
 
export default Listing;
