import React, {Component} from 'react';

async function fetch_query(q)
    {
        let res

        res = await fetch(q)

        let results = await res.json()
        console.log(results)
        return results;
        
    }

class Listing extends Component {
    constructor(props) {
        super(props);
        this.state = 
        { 
            query : 'https://www.breakingbadapi.com/api/characters?limit=10', 
            result : ''
        }

    }

    handleClick = () =>
    {
       let result1 =  fetch_query(this.state.query)

       this.setState({
           result : result1
       })

    }

    render() { 

        return (
            <div>
                <button onClick = {this.handleClick} onLoad= {this.handleClick}>Find</button>
                {
                    this.state.result.map(x =><p>{x.name}</p>)
                }
            </div>
            
        
        );
    }
}
 
export default Listing;
